option.value:
pub mod config;
pub mod discord;
pub mod commands;

pub use config::Config;
use std::env;

#[derive(Debug, Clone)]
pub struct Config {
    pub discord_app_id: String,
    pub discord_guild_id: String,
    pub discord_bot_token: String,
    pub discord_public_key: String,
    pub minecraft_link_api_url: String,
    pub secret_key: String,
}

impl Config {
    pub fn from_env() -> Result<Self, String> {
        Ok(Self {
            discord_app_id: env::var("DISCORD_APP_ID")
                .map_err(|_| "Missing DISCORD_APP_ID environment variable")?,
            discord_guild_id: env::var("DISCORD_GUILD_ID")
                .map_err(|_| "Missing DISCORD_GUILD_ID environment variable")?,
            discord_bot_token: env::var("DISCORD_BOT_TOKEN")
                .map_err(|_| "Missing DISCORD_BOT_TOKEN environment variable")?,
            discord_public_key: env::var("DISCORD_PUBLIC_KEY")
                .map_err(|_| "Missing DISCORD_PUBLIC_KEY environment variable")?,
            minecraft_link_api_url: env::var("MINECRAFT_LINK_API_URL")
                .unwrap_or_else(|_| "http://localhost:25565/link".to_string()),
            secret_key: env::var("SECRET_KEY")
                .unwrap_or_else(|_| "default_secret".to_string()),
        })
    }

    pub fn commands_url(&self) -> String {
        format!(
            "https://discord.com/api/v10/applications/{}/guilds/{}/commands",
            self.discord_app_id, self.discord_guild_id
        )
    }

    pub fn auth_header(&self) -> String {
        format!("Bot {}", self.discord_bot_token)
    }
}

pub mod config;
pub mod discord;
pub mod commands;

pub use config::Config;
